                },
            ]
        }
    }

    componentDidMount() {
        if (this.props.url.query) {
            this.setState({
                filter: this.props.url.query.site_admin ? this.props.url.query.site_admin : false
            }, () => this.preFillData())
        }
    }

    componentDidUpdate(prevProps) {
        if (this.props.url.query.site_admin !== prevProps.url.query.site_admin)
            this.setState({
                filter: this.props.url.query.site_admin ? this.props.url.query.site_admin : false
            }, () => this.preFillData())
    }

    preFillData = () => {
        const userList = [...this.state.userList],
            data = userList.filter((el) => {
                return el.site_admin === this.state.filter
            })
        this.setState({
            userList: data
        })
    }

    handleClick = (filter) => {
        Router.pushRoute('/about?site_admin=' + filter)
    }


    render() {
        console.log("fillter", this.state.filter)
        return (
            <div style={{ textAlign: "center" }}>
                <button type="button" onClick={() => this.handleClick(true)}>Add Fillter</button>
                <button type="button" onClick={() => this.handleClick(false)}>Remove Fillter</button>
                {this.state.userList.map((i) => {
                    return (
                        <div key={i.id}>
                            {i.login}
                        </div>
                    )
                })}
            </div>
        );
    }
}

export default AboutUs;
