                },
                {
                    "login": "takeo",
                    "type": "superUser",
                },
                {
                    "login": "caged",
                    "type": "User",
                },
                {
                    "login": "topfunky",
                    "type": "superUser",
                },
                {
                    "login": "anotherjesse",
                    "type": "User",
                },
                {
                    "login": "roland",
                    "type": "User",
                },
                {
                    "login": "lukas",
                    "type": "superUser",
                },
                {
                    "login": "fanvsfan",
                    "type": "User",
                },
            ]
        }
    }

    componentDidMount() {
        if (this.props.url.query) {
            this.setState({
                filter: this.props.url.query.userType ? this.props.url.query.userType : "User"
            }, () => this.preFillData())
        }
    }

    componentDidUpdate(prevProps) {
        if (this.props.url.query.userType !== prevProps.url.query.userType)
            this.setState({
                filter: this.props.url.query.userType ? this.props.url.query.userType : "User"
            }, () => this.preFillData())
    }

    preFillData = () => {
        console.log(this.state.filter)
        const userList = [...this.state.userList], data = [];
        if (this.state.filter === "superUser") {
            userList.map((i) => {
                if (i.type === "superUser") {
                    data.push(i);
                }
                return i
            })
        }
        this.setState({
            userList: data.length > 0 ? data : userList
        })
    }

    handleClick = (user) => {
        Router.pushRoute('/about?userType=' + user)
    }


    render() {
        console.log("fillter", this.state.userList)
        return (
            <div style={{ textAlign: "center" }}>
                This is about page
            </div>
            );
    }
}

export default AboutUs;
