import React, { Component } from 'react';
import { Router } from '../routes'
const queryString = require('query-string');


class AboutUs extends Component {
    constructor(props) {
        super(props)
        this.state = {
            filter: false,
            userList: [
                {
                    "login": "mojombo",
                    "id": 1,
                    "type": "User",
                    "site_admin": true
                },
                {
                    "login": "defunkt",
